import React from 'react';
import styles from './MealsSummary.module.css';

const MealsSummary = (props) => {
  return (
    <div className={styles['meals-summary']}>
      <h1>
        The Best Fast <span>Food</span> in Your City
      </h1>
      <p>
        Chose your favourite meal from our broad selection of available meals
        and enjoy a delicious lunch or dinner at home
      </p>
      <p>ORDER YOUR FOOD HERE</p>
      <p>
        Down arrow: <i className="arrow down"></i>
      </p>
    </div>
  );
};

export default MealsSummary;
